import { useState, useEffect } from 'react';
import './App.css';

const App = () => {
  let [count, setCount] = useState(0);
  useEffect(() => {
    alert(`You've changed the counter to ${count}`);
  },[count]);
  return (
    <div className="App">
      <button onClick={() => setCount((prevCount) => prevCount - 1)}><h2>-</h2></button>
      <h1>{ count }</h1>
      <button onClick={() => setCount((prevCount) => prevCount + 1)}><h2>+</h2></button>
    </div> 
  );
}

export default App;
